package Lec5;

public class typecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		int a = 047;
//		System.out.println(a);
		byte b = (byte) (140);
		b = 7;
		System.out.println(b);
		byte b1 = (byte) (428);
		System.out.println(b1);
		
		int i = 17;
		i = b;
		b = -5;
		i = b;
		System.out.println(i);
		long l = 55665258523l;
		System.out.println(l);
	}

}
